mapper.select:

select 

from ABACUS.ABA_PD_AMOUNT_BY_DEPOSIT
where DEPOSIT_SET_ID = #{depositSetId,jdbcType=VARCHAR}
  and DEPOSIT_ID = #{depositId,jdbcType=VARCHAR}
  and CUST_TYPE = #{custType,jdbcType=VARCHAR}
  and DEPOSIT = #{deposit,jdbcType=DECIMAL}
  and THRESHOLD = #{threshold,jdbcType=DECIMAL}

mapper.delete:

delete from ABACUS.ABA_PD_AMOUNT_BY_DEPOSIT
where DEPOSIT_SET_ID = #{depositSetId,jdbcType=VARCHAR}
  and DEPOSIT_ID = #{depositId,jdbcType=VARCHAR}
  and CUST_TYPE = #{custType,jdbcType=VARCHAR}
  and DEPOSIT = #{deposit,jdbcType=DECIMAL}
  and THRESHOLD = #{threshold,jdbcType=DECIMAL}

mapper.update:

update ABACUS.ABA_PD_AMOUNT_BY_DEPOSIT


    THRESHOLD_MAX = #{thresholdMax,jdbcType=DECIMAL},


    THRESHOLD_UNIT = #{thresholdUnit,jdbcType=VARCHAR},


    SYS_CREATION_DATE = #{sysCreationDate,jdbcType=TIMESTAMP},


    SYS_UPDATE_DATE = #{sysUpdateDate,jdbcType=TIMESTAMP},


    OPERATOR_ID = #{operatorId,jdbcType=VARCHAR},


    APPLICATION_ID = #{applicationId,jdbcType=VARCHAR},


    DL_SERVICE_CODE = #{dlServiceCode,jdbcType=VARCHAR},


    DL_UPDATE_STAMP = #{dlUpdateStamp,jdbcType=DECIMAL},


    THRESHOLD_LIST = #{thresholdList,jdbcType=VARCHAR},


where DEPOSIT_SET_ID = #{depositSetId,jdbcType=VARCHAR}
  and DEPOSIT_ID = #{depositId,jdbcType=VARCHAR}
  and CUST_TYPE = #{custType,jdbcType=VARCHAR}
  and DEPOSIT = #{deposit,jdbcType=DECIMAL}
  and THRESHOLD = #{threshold,jdbcType=DECIMAL}


update ABACUS.ABA_PD_AMOUNT_BY_DEPOSIT
set THRESHOLD_MAX = #{thresholdMax,jdbcType=DECIMAL},
  THRESHOLD_UNIT = #{thresholdUnit,jdbcType=VARCHAR},
  SYS_CREATION_DATE = #{sysCreationDate,jdbcType=TIMESTAMP},
  SYS_UPDATE_DATE = #{sysUpdateDate,jdbcType=TIMESTAMP},
  OPERATOR_ID = #{operatorId,jdbcType=VARCHAR},
  APPLICATION_ID = #{applicationId,jdbcType=VARCHAR},
  DL_SERVICE_CODE = #{dlServiceCode,jdbcType=VARCHAR},
  DL_UPDATE_STAMP = #{dlUpdateStamp,jdbcType=DECIMAL},
  THRESHOLD_LIST = #{thresholdList,jdbcType=VARCHAR}
where DEPOSIT_SET_ID = #{depositSetId,jdbcType=VARCHAR}
  and DEPOSIT_ID = #{depositId,jdbcType=VARCHAR}
  and CUST_TYPE = #{custType,jdbcType=VARCHAR}
  and DEPOSIT = #{deposit,jdbcType=DECIMAL}
  and THRESHOLD = #{threshold,jdbcType=DECIMAL}
