mapper.select:

select 

from ABACUS.ABA_PD_CUSTOM_RC
where INVENTORY_ITEM_ID = #{inventoryItemId,jdbcType=DECIMAL}
  and VALD_STRT_DT = #{valdStrtDt,jdbcType=TIMESTAMP}
  and PROD_CD = #{prodCd,jdbcType=VARCHAR}
  and RC_INDEX = #{rcIndex,jdbcType=DECIMAL}

mapper.delete:

delete from ABACUS.ABA_PD_CUSTOM_RC
where INVENTORY_ITEM_ID = #{inventoryItemId,jdbcType=DECIMAL}
  and VALD_STRT_DT = #{valdStrtDt,jdbcType=TIMESTAMP}
  and PROD_CD = #{prodCd,jdbcType=VARCHAR}
  and RC_INDEX = #{rcIndex,jdbcType=DECIMAL}
